mapper.select:
select * from
  exam_info ,exam_location,exam_room
where exam_info.kaoci_id = #{kaociId,jdbcType=INTEGER}
  and exam_info.examroom_id = exam_room.examroom_id
  and exam_room.location_code = exam_location.location_code
  and exam_location.district_id = #{districtId,jdbcType=INTEGER}

select * from
  exam_info
where exam_info.examroom_id = #{examroomId,jdbcType=INTEGER}
  and exam_info.kaoci_id = #{kaociId,jdbcType=INTEGER}

mapper.update:
update exam_info
set seat = #{seat,jdbcType=INTEGER},
  examinee_id = #{examineeId,jdbcType=VARCHAR},
  examroom_id = #{examroomId,jdbcType=INTEGER},
  kaoci_id = #{kaociId,jdbcType=INTEGER}
where exam_info_id = #{examInfoId,jdbcType=INTEGER}
